import { useState } from 'react';
import { useTaskContext } from '../context/TaskContext';

export default function MessageForm({ setIsLoading, isLoading }) {
  const { dispatch } = useTaskContext();
  const [formData, setFormData] = useState({
    cookies: '',
    friendIds: '',
    message: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:3001/api/send-messages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          cookies: JSON.parse(formData.cookies),
          friendIds: formData.friendIds.split(',').map(id => id.trim()),
          message: formData.message
        }),
      });

      const data = await response.json();
      
      if (response.ok) {
        dispatch({
          type: 'ADD_TASK',
          payload: {
            id: data.taskId,
            status: 'queued',
            timestamp: new Date().toISOString(),
            ...formData
          }
        });

        // Start polling for task status
        const pollInterval = setInterval(async () => {
          const statusResponse = await fetch(`http://localhost:3001/api/task/${data.taskId}`);
          const statusData = await statusResponse.json();
          
          if (statusData.state === 'completed' || statusData.state === 'failed') {
            clearInterval(pollInterval);
            dispatch({
              type: 'UPDATE_TASK',
              payload: {
                id: data.taskId,
                status: statusData.state,
                result: statusData.result
              }
            });
          }
        }, 5000);
      } else {
        throw new Error(data.error || 'Failed to send messages');
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6 bg-white shadow-sm rounded-lg p-6">
      <div>
        <label className="block text-sm font-medium text-gray-700">
          Cookies (JSON format)
        </label>
        <textarea
          required
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          rows={4}
          value={formData.cookies}
          onChange={(e) => setFormData({ ...formData, cookies: e.target.value })}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Friend IDs (comma-separated)
        </label>
        <input
          type="text"
          required
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          value={formData.friendIds}
          onChange={(e) => setFormData({ ...formData, friendIds: e.target.value })}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">
          Message
        </label>
        <textarea
          required
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          rows={3}
          value={formData.message}
          onChange={(e) => setFormData({ ...formData, message: e.target.value })}
        />
      </div>

      <button
        type="submit"
        disabled={isLoading}
        className={`w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${
          isLoading ? 'opacity-50 cursor-not-allowed' : ''
        }`}
      >
        {isLoading ? 'Processing...' : 'Send Messages'}
      </button>
    </form>
  );
}
